@Component({
  selector: 'app-layout-home',
  templateUrl: './layout-home.component.html',
  styleUrls: ['./layout-home.component.css']
})

export class LayoutHomeComponent {

  rutaFoto: String = "../../assets/fondo.jpg";
}
<img class="background" src={{rutaFoto}}>
<p>foto aquí</p>
