@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'hola mundo de nuevo...';

  contador:number=0;

  sumar()
  {
    this.contador++;
  }

}
