import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';


/* interfaces */

import { ProductEntity } from '../interface/productEntity';
import { environment } from 'src/environments/environment';
import { Autorizacion } from '../interface/autorizacion';

@Injectable({
  providedIn: 'root'
})

export class ProductService {

  server:String = "http://localhost:8080/";

  constructor(private http:HttpClient) { }

  SearchBarRequest(aBuscar: String){
    return this.http.get<ProductEntity[]>(this.server+'home/products/names/'+ aBuscar);
  }

}
